#Given a binary array, find the maximum number of consecutive 1s in this array.

def findMaxConsecutiveOnes(nums):
    maxOnes = 0
    tempOnes = 0
    for number in nums:
        if number == 1:
            tempOnes += 1
        else:
            if tempOnes > maxOnes:
                maxOnes = tempOnes
            tempOnes = 0
    if tempOnes > maxOnes:
        maxOnes = tempOnes
    return maxOnes

